import React, { useState } from 'react';

function LoginForm({ login }) {
  const [formData, setFormData] = useState({
    username: "", // Pre-fill these for development if needed
    password: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(data => ({ ...data, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    login(formData);
  };

  return (
    <div className="LoginForm">
      <div className="container col-md-6 offset-md-3 col-lg-4 offset-lg-4">
        <h2 className="mb-3">Log In</h2>
        <div className="card">
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Username</label>
                <input
                  name="username"
                  className="form-control"
                  value={formData.username}
                  onChange={handleChange}
                  placeholder="Username"  // Optional: you can remove the placeholder since you have a label
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  className="form-control"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Password"  // Optional: you can remove the placeholder since you have a label
                />
              </div>
              <button type="submit" className="btn btn-primary float-right">
                Login
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );  
}

export default LoginForm;
